import React, { Component } from 'react';
var _ = require('lodash');
// import Icon, { FontAwesome, Feather } from 'react-web-vector-icons';

class CollaboratorList extends Component {
  constructor(props) {
    super(props);
  
    this.state = {
      collaboratorInputText: [],
      collaboratorText: ''
    };
  }

  deleteCollaborator = (item) => {
    const {db, favorite, collaborators} = this.props;
    let getItemIndex = favorite.collaborator.indexOf(item);

    let tempCollaborator = {...collaborators[favorite.id]}
    tempCollaborator.splice(getItemIndex, 1);
    let favoriteRef = db.collection('favorites');
    favoriteRef.doc(favorite.id).update({collaborator: tempCollaborator}).then(resp => {
      this.props.setCollaborator(favorite.id, tempCollaborator);
    });
  };

  toggleCollaborator = (favoriteId, bool) => {
    this.setState({ collaboratorInputText: {[favoriteId]: bool} })
  };

  handleInputCollaborator = (event) => {
    this.setState({collaboratorText: event.target.value})
  };

  addCollaborator = () => {
    const {db, favorite, collaborators} = this.props;
    const {collaboratorText} = this.state;
    let newValue = [...collaborators[favorite.id], collaboratorText];
    let favoriteRef = db.collection('favorites');
    favoriteRef.doc(favorite.id).update({collaborator: newValue}).then(resp => {
      // this.props.addCollaborator(favorite.id, collaboratorText);
      this.props.setCollaborator(favorite.id, newValue);
      this.toggleCollaborator(favorite.id, false)
    });

  };

  render() {
    const {favorite, collaborators} = this.props;
    const {collaboratorInputText} = this.state;

    const renderCollaborator = () => {
      return collaborators[favorite.id].map((item, key) => 
        <div key={key} style={{display: 'inline-block', border: '1px solid black', borderRadius: 10, marginLeft: 4, marginRight: 4}}>
          <div style={{display: 'inline-block', padding: '2px 8px 2px 8px'}}>
            <label>{item}</label>
          </div>
          <div style={{display: 'inline-block', padding: '2px 8px 2px 8px'}} onClick={() => this.deleteCollaborator(item)}>
            <label>x</label>
          </div>
        </div>
      )
    };

    const renderAddCollaborator = () => {
      return (
        collaboratorInputText[favorite.id] ?
        <div style={{display: 'inline-block', marginLeft: 4}}>
          <input type="text" onChange={this.handleInputCollaborator} />
          <button onClick={this.addCollaborator}>Ok</button>
        </div>
        :
        <div onClick={() => this.toggleCollaborator(favorite.id, true)} style={{display: 'inline-block', border: '1px solid black', borderRadius: 10, marginLeft: 4, marginRight: 4}}>
          <div style={{display: 'inline-block', padding: '2px 8px 2px 8px'}}>
            <label>+</label>
          </div>
          <div style={{display: 'inline-block', padding: '2px 8px 2px 8px'}}>
            <label>Add Collaborator</label>
          </div>
        </div>
      )
    }

    return (
      <div>
        {_.isEmpty(collaborators) === false ? renderCollaborator() : null}
        {_.isEmpty(collaborators) === false ? renderAddCollaborator() : null}
      </div>
    );
  }
}

export default CollaboratorList;
