import React, { Component } from 'react';
import "react-datepicker/dist/react-datepicker.css";

class AddFavorite extends Component {
  constructor(props) {
    super(props);
  
    this.state = {
    };
  }

  handleChange = (event) => {
    this.setState({favoriteAs: event.target.value})
  };

  addToFavorite = () => {
    alert(this.state.favoriteAs)
    const {db} = this.props;

    let newItem = {};

    let restaurantRef = db.collection('favorites');
    restaurantRef.add(newItem).then(ref => {
      db.collection("restaurants").doc(ref.id).update({id: ref.id});
    })
  };
 
  render() {
    return (
      <div style={{display: 'flex', flex: 1, flexDirection: 'row'}}>
        <div style={{flex: 1}} onClick={this.addToFavorite}>
          <label>add to favorite</label>
        </div>
        <div style={{flex: 1}}>
          <input type="text" onChange={this.handleChange}></input>
        </div>
      </div>
    );
  }
}

export default AddFavorite;
