import React, { Component } from 'react';
import "react-datepicker/dist/react-datepicker.css";

class AddFavorite extends Component {
  constructor(props) {
    super(props);
  
    this.state = {
    };
  }

  handleChange = (event) => {
    this.setState({favoriteAs: event.target.value})
  };

  addToFavorite = () => {
    alert(this.state.favoriteAs)
    let restaurantRef = this.props.db.collection('restaurants');
    restaurantRef.get().then((querySnapshot) => {
      // this.setState({ restaurantList: querySnapshot }, () => {
      //   console.log(this.state.restaurantList)
      // });
      querySnapshot.forEach((doc) => {
        // this.setState({ restaurantList: [...this.state.restaurantList, doc.data()]})
        this.setState({ 
          restaurantList: {
            ...this.state.restaurantList,
            [doc.id]: doc.data()
          }
        });
      });
    })
    .catch((error) => {
      console.log("Error getting documents: ", error);
    });
  };
 
  render() {
    return (
      <div style={{display: 'flex', flex: 1, flexDirection: 'row'}}>
        <div style={{flex: 1}} onClick={this.addToFavorite}>
          <label>add to favorite</label>
        </div>
        <div style={{flex: 1}}>
          <input type="text" onChange={this.handleChange}></input>
        </div>
      </div>
    );
  }
}

export default AddFavorite;
