import React, { Component } from 'react';
import DatePicker from "react-datepicker";
import TimeInput from 'react-time-input';
import "react-datepicker/dist/react-datepicker.css";
import RestaurantList from './RestaurantList';

const firebase = require("firebase");
// Required for side-effects
require("firebase/firestore");

firebase.initializeApp({
  apiKey: "AIzaSyB9G_v0FY2aFN3VzG9J10TI_wtSc-hEFBg",
  authDomain: "interview-test-ec9c0.firebaseapp.com",
  databaseURL: "https://interview-test-ec9c0.firebaseio.com",
  projectId: "interview-test-ec9c0",
  storageBucket: "interview-test-ec9c0.appspot.com",
  messagingSenderId: "493975126354"
});

// Initialize Cloud Firestore through Firebase
var db = firebase.firestore();
var _ = require('lodash');

// Disable deprecated features
db.settings({
  timestampsInSnapshots: true
});

const dayLibs = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

class Home extends Component {
  constructor(props) {
    super(props);
  
    this.state = {
      selectedDate: new Date(),
      selectedTime: '00:00',
      restaurantList: [],
      filteredRestaurantList: []
    };

    this.getData();
  }

  // getData = () => {
  //   // restaurantRef.where("capital", "==", true)
  //   var restaurantRef = db.collection('restaurants');
  //   restaurantRef.get().then((querySnapshot) => {
  //     querySnapshot.forEach((doc) => {
  //       // console.log(doc.id, " => ", doc.data());
  //     });
  //   })
  //   .catch((error) => {
  //     console.log("Error getting documents: ", error);
  //   });
  // };

  getData = () => {
    // restaurantRef.where("capital", "==", true)
    var restaurantRef = db.collection('restaurants');
    restaurantRef.get().then((querySnapshot) => {
      // this.setState({ restaurantList: querySnapshot }, () => {
      //   console.log(this.state.restaurantList)
      // });
      querySnapshot.forEach((doc) => {
        this.setState({ restaurantList: [...this.state.restaurantList, doc.data()]})
      });
    })
    .catch((error) => {
      console.log("Error getting documents: ", error);
    });
  };

  onDateChange = (selectedDate) => {
    console.log(selectedDate.getDay())
    this.setState({selectedDate}, () => {
      console.log(selectedDate.getDay())
      console.log(dayLibs[selectedDate.getDay()])
      let selectedDay = dayLibs[selectedDate.getDay()];
      let filteredRestaurantList = _.filter(this.state.restaurantList, (x) => {
        console.log(selectedDay)
        if(x.schedules[selectedDay]){
          return (x.schedules[selectedDay].hourStart <= this.state.selectedTime && x.schedules[selectedDay].hourEnd >= this.state.selectedTime)
        }
        else{
          return false
        }
      });
      console.log(filteredRestaurantList)
      this.setState({ filteredRestaurantList})
    });
    console.log(this.state.restaurantList)
  };

  onTimeChange = (selectedTime) => {
    this.setState({selectedTime})
  };
 
  render() {
    return (
      <div>
        <div>
        <DatePicker
          selected={this.state.selectedDate}
          onChange={this.onDateChange} />
        <TimeInput
          initTime={this.state.selectedTime}
          ref="TimeInputWrapper"
          className='form-control'
          mountFocus='true'
          onTimeChange={this.onTimeChange} />
        </div>
        <div>
          <RestaurantList data={this.state.filteredRestaurantList} />
        </div>
      </div>
    );
  }
}

export default Home;
