import React, { Component } from 'react';
import DatePicker from "react-datepicker";
import TimeInput from 'react-time-input';
import "react-datepicker/dist/react-datepicker.css";
import RestaurantList from './RestaurantList';

var _ = require('lodash');
const dayLibs = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

class Home extends Component {
  constructor(props) {
    super(props);
  
    this.state = {
      selectedDate: new Date(),
      selectedTime: '00:00',
      restaurantList: {},
      filteredRestaurantList: {}
    };

    this.getData();
  }

  getData = () => {
    const {db} = this.props;
    var restaurantRef = db.collection('restaurants');
    restaurantRef.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        // this.setState({ restaurantList: [...this.state.restaurantList, doc.data()]})
        this.setState({ 
          restaurantList: {
            ...this.state.restaurantList,
            [doc.id]: doc.data()
          }
        });
      });
    })
    .catch((error) => {
      console.log("Error getting documents: ", error);
    });
  };

  onDateChange = (selectedDate) => {
    console.log(selectedDate.getDay())
    this.setState({selectedDate}, () => {
      this.inquiryRestaurant();
    });
    console.log(this.state.restaurantList)
  };

  onTimeChange = (selectedTime) => {
    this.setState({selectedTime}, () => {
      this.inquiryRestaurant();
    })
  };

  inquiryRestaurant = () => {
    let selectedDay = dayLibs[this.state.selectedDate.getDay()];
      let filteredRestaurantList = _.filter(this.state.restaurantList, (x) => {
      console.log(selectedDay)
      if(x.schedules[selectedDay]){
        console.log('name: ', x.name)
        console.log('time: ', this.state.selectedTime)
        console.log('schedule: ', x.schedules)
        console.log('condL ', x.schedules[selectedDay].hourStart <= this.state.selectedTime && x.schedules[selectedDay].hourEnd >= this.state.selectedTime)
        let isOvernight = x.schedules[selectedDay].hourStart > x.schedules[selectedDay].hourEnd ? true : false;
        console.log('isOvernight:', isOvernight)
        if(isOvernight){
          return (x.schedules[selectedDay].hourStart <= this.state.selectedTime || x.schedules[selectedDay].hourEnd >= this.state.selectedTime)
        }
        else{
          return (x.schedules[selectedDay].hourStart <= this.state.selectedTime && x.schedules[selectedDay].hourEnd >= this.state.selectedTime)
        }
      }
      else{
        return false
      }
    });
    console.log(filteredRestaurantList)
    this.setState({ filteredRestaurantList })
  };
 
  render() {
    const {db} = this.props;

    // const renderContent = () => {
    //   let content;
    //   switch(this.state.tab){
    //     case 'INQUIRY':
    //       content = <RestaurantList data={this.state.filteredRestaurantList} db={db} />
    //       break;
    //     case 'FAVORITE':
    //       content = <div />
    //       break;
    //     default:
    //       content = <RestaurantList data={this.state.filteredRestaurantList} db={db} />
    //   }

    //   return content;
    // }

    return (
      <div>
        <div>
          <label>Input date: </label>
          <DatePicker
            selected={this.state.selectedDate}
            onChange={this.onDateChange} />
          <label>Input time: </label>
          <TimeInput
            initTime={this.state.selectedTime}
            ref="TimeInputWrapper"
            className='form-control'
            mountFocus='true'
            onTimeChange={this.onTimeChange} />
        </div>
        <div>
          <button onClick={this.gotoInquiry}>INQUIRY</button>
          <button onClick={this.gotoFavorite}>FAVORITE</button>
        </div>
        <div>
          <RestaurantList data={this.state.filteredRestaurantList} db={db} />
        </div>
      </div>
    );
  }
}

export default Home;
