import React, { Component } from 'react';
import DatePicker from "react-datepicker";
import TimeInput from 'react-time-input';
import "react-datepicker/dist/react-datepicker.css";
import RestaurantList from './RestaurantList';
import FavoriteListContainer from './FavoriteListContainer';

var _ = require('lodash');
const dayLibs = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

class Home extends Component {
  constructor(props) {
    super(props);
  
    this.state = {
      selectedDate: new Date(),
      selectedTime: '00:00',
      restaurantList: {},
      favoriteList: [],
      filteredRestaurantList: []
    };

    this.getRestaurants();
    // this.getFavorites();
  }

  getRestaurants = () => {
    const {db} = this.props;
    let restaurantRef = db.collection('restaurants');
    restaurantRef.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        this.setState({ 
          restaurantList: {
            ...this.state.restaurantList,
            [doc.id]: doc.data()
          }
        });
      });
    })
    .catch((error) => {
      console.log("Error getting restaurants: ", error);
    });
  };

  // getFavorites = (callback) => {
  //   const {db, user} = this.props;
  //   let favoriteRef = db.collection('favorites');
  //   let favoriteList = [];

  //   favoriteRef.get().then((querySnapshot) => {
  //     querySnapshot.forEach((doc) => {
  //       if(doc.data().owner === user || doc.data().collaborator.includes(user)){
  //         favoriteList.push(doc.data())
  //       }
  //     });
  //     this.props.setFavorites(favoriteList);
  //     this.setState({favoriteList}, () => {
  //       if(typeof callback === 'function'){
  //         callback();
  //       }
  //     })
  //   })
  //   .catch((error) => {
  //     console.log("Error getting favorites: ", error);
  //   });
  // };

  onDateChange = (selectedDate) => {
    console.log(selectedDate.getDay())
    this.setState({selectedDate}, () => {
      this.inquiryRestaurant();
    });
    console.log(this.state.restaurantList)
  };

  onTimeChange = (selectedTime) => {
    this.setState({selectedTime}, () => {
      this.inquiryRestaurant();
    })
  };

  inquiryRestaurant = () => {
    let selectedDay = dayLibs[this.state.selectedDate.getDay()];
    let filteredRestaurantList = _.filter(this.state.restaurantList, (x) => {
      console.log(selectedDay)
      if(x.schedules[selectedDay]){
        let isOvernight = x.schedules[selectedDay].hourStart > x.schedules[selectedDay].hourEnd ? true : false;
        
        if(isOvernight){
          return (x.schedules[selectedDay].hourStart <= this.state.selectedTime || x.schedules[selectedDay].hourEnd >= this.state.selectedTime)
        }
        else{
          return (x.schedules[selectedDay].hourStart <= this.state.selectedTime && x.schedules[selectedDay].hourEnd >= this.state.selectedTime)
        }
      }
      else{
        return false
      }
    });
    this.setState({ filteredRestaurantList })
    this.props.setRestaurants(filteredRestaurantList)
  };

  gotoInquiry = () => {
    this.setState({tab: 'INQUIRY'})
  };

  gotoFavorite = () => {
    this.setState({tab: 'FAVORITE'})
  };

  handleRemove = (item, favoriteKey, favorite) => {
    const {db} = this.props;
    let newList = _.filter(favorite.restaurants, (x) => {
      return item.id !== x.id
    });
    let favoriteRef = db.collection('favorites');
    favoriteRef.doc(favoriteKey).update({restaurants: newList}).then(resp => {
      this.getFavorites();
    });
  };

  handleEdit = (key, editTo, callback) => {
    const {db} = this.props;
    let favoriteRef = db.collection('favorites');
    favoriteRef.doc(key).update({name: editTo}).then(resp => {
      this.getFavorites(callback);
    });
  };
 
  render() {
    const {db, user, favorites} = this.props;

    const renderInquiry = (
      <div>
        <div>
          <label>Input date: </label>
          <DatePicker
            selected={this.state.selectedDate}
            onChange={this.onDateChange} />
          <label>Input time: </label>
          <TimeInput
            initTime={this.state.selectedTime}
            ref="TimeInputWrapper"
            className='form-control'
            mountFocus='true'
            onTimeChange={this.onTimeChange} />
        </div>
        <div>
          <RestaurantList data={this.props.restaurants} db={db} user={user} />
        </div>
      </div>
    )

    const renderFavorite = (
      <div>
        {
          favorites ? <FavoriteListContainer db={db} handleRemove={this.handleRemove} handleEdit={this.handleEdit} /> : null
          // this.state.favoriteList ? <FavoriteListContainer data={this.state.favoriteList} db={db} user={user} handleRemove={this.handleRemove} handleEdit={this.handleEdit} /> : null
        }
      </div>
    )

    const renderContent = () => {
      let content;
      switch(this.state.tab){
        case 'INQUIRY':
          content = renderInquiry
          break;
        case 'FAVORITE':
          content = renderFavorite
          break;
        default:
          content = renderInquiry
      }

      return content;
    }

    return (
      <div>
        <div>
          <button onClick={this.gotoInquiry}>INQUIRY</button>
          <button onClick={this.gotoFavorite}>FAVORITE</button>
        </div>
        {renderContent()}
      </div>
    );
  }
}

export default Home;
