import React, { Component } from 'react';
import './App.css';
import Home from './Home.js';
import Login from './Login.js';
const firebase = require("firebase");
// Required for side-effects
require("firebase/firestore");

firebase.initializeApp({
  apiKey: "AIzaSyB9G_v0FY2aFN3VzG9J10TI_wtSc-hEFBg",
  authDomain: "interview-test-ec9c0.firebaseapp.com",
  databaseURL: "https://interview-test-ec9c0.firebaseio.com",
  projectId: "interview-test-ec9c0",
  storageBucket: "interview-test-ec9c0.appspot.com",
  messagingSenderId: "493975126354"
});

// Initialize Cloud Firestore through Firebase
var db = firebase.firestore();

// Disable deprecated features
db.settings({
  timestampsInSnapshots: true
});

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      loggedIn: false
    };
  }

  handleLogin = (email) => {
    let userRef = db.collection('users').doc(email);
    userRef.get().then(resp => {
      if(!resp.exists){
        userRef.set({email}).then(ref => {
          this.setState({loggedIn: true}, () => alert(email))
        });
      }
      else{
        this.setState({loggedIn: true}, () => alert(email))
      }
    }).catch(err => {
      console.log('Error getting document', err);
    });
    // userRef.doc(email).set(newItem).then(ref => {
    //   this.setState({loggedIn: true}, () => alert(email))
    // });

  }

  render() {
    return (
      <div className="App">
        {
          this.state.loggedIn ? <Home db={db} /> : <Login db={db} handleLogin={this.handleLogin} />
        }
      </div>
    );
  }
}

export default App;
