import React, { Component } from 'react';
// import Icon, { FontAwesome, Feather } from 'react-web-vector-icons';

class CollaboratorList extends Component {
  constructor(props) {
    super(props);
  
    this.state = {
      collaboratorInputText: [],
      collaboratorText: ''
    };
  }

  removeItem = () => {

  };

  toggleCollaborator = (favoriteId, key) => {
    // this.setState({collaboratorInputText: {[favoriteId]: true});
    alert(favoriteId);
    alert(key);
    this.setState({ collaboratorInputText: {[favoriteId]: true} })
  };

  handleInputCollaborator = (event) => {
    this.setState({collaboratorText: event.target.value})
  };

  render() {
    const {favorite, user, key} = this.props;
    const {collaboratorInputText} = this.state;

    const renderCollaborator = () => {
      return favorite.collaborator.map((item, key) => 
        <div key={key} style={{display: 'inline-block', border: '1px solid black', borderRadius: 10, marginLeft: 4, marginRight: 4}}>
          <div style={{display: 'inline-block', padding: '2px 8px 2px 8px'}}>
            <label>{item}</label>
          </div>
          <div style={{display: 'inline-block', padding: '2px 8px 2px 8px'}} onClick={this.removeItem}>
            <label>x</label>
          </div>
        </div>
      )
    };

    const renderAddCollaborator = () => {
      return (
        collaboratorInputText[favorite.id] ?
        <div>
          <input type="text" onChange={this.handleInputCollaborator} />
        </div>
        :
        <div onClick={() => this.toggleCollaborator(favorite.id, key)} style={{display: 'inline-block', border: '1px solid black', borderRadius: 10, marginLeft: 4, marginRight: 4}}>
          <div style={{display: 'inline-block', padding: '2px 8px 2px 8px'}}>
            <label>+</label>
          </div>
          <div style={{display: 'inline-block', padding: '2px 8px 2px 8px'}}>
            <label>Add Collaborator</label>
          </div>
        </div>
      )
    }

    return (
      <div>
        {renderCollaborator()}
        {renderAddCollaborator()}
      </div>
    );
  }
}

export default CollaboratorList;
