import React, { Component } from 'react';
// import Icon, { FontAwesome, Feather } from 'react-web-vector-icons';

class CollaboratorList extends Component {
  constructor(props) {
    super(props);
  
    this.state = {
      toggleEdit: []
    };
  }

  removeItem = () => {

  };

  addCollaborator = () => {

  };

  render() {
    const {favorite, user} = this.props;

    const renderCollaborator = () => {
      return favorite.collaborator.map((item, key) => 
        <div style={{display: 'inline-block', border: '1px solid black', borderRadius: 10, marginLeft: 4, marginRight: 4}}>
          <div style={{display: 'inline-block', padding: '2px 8px 2px 8px'}}>
            <label>{item}</label>
          </div>
          <div style={{display: 'inline-block', padding: '2px 8px 2px 8px'}} onClick={this.removeItem}>
            <label>x</label>
          </div>
        </div>
      )
    };

    const renderAddCollaborator = () => {
      return (
        <div onClick={this.addCollaborator} style={{display: 'inline-block', border: '1px solid black', borderRadius: 10, marginLeft: 4, marginRight: 4}}>
          <div style={{display: 'inline-block', padding: '2px 8px 2px 8px'}}>
            <label>+</label>
          </div>
          <div style={{display: 'inline-block', padding: '2px 8px 2px 8px'}}>
            <label>Add Collaborator</label>
          </div>
        </div>
      )
    }

    return (
      <div>
        {renderCollaborator()}
        {renderAddCollaborator()}
      </div>
    );
  }
}

export default CollaboratorList;
