import React, { Component } from 'react';
import Collaborator from './Collaborator.js'

class FavoriteList extends Component {
  constructor(props) {
    super(props);
  
    this.state = {
      toggleEdit: []
    };

    this.getFavorites();
  }

  getFavorites = (callback) => {
    const {db, user} = this.props;
    let favoriteRef = db.collection('favorites');
    let favoriteList = [];

    favoriteRef.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        if(doc.data().owner === user || doc.data().collaborator.includes(user)){
          favoriteList.push(doc.data())
        }
      });
      this.props.setFavorites(favoriteList);
      this.setState({favoriteList}, () => {
        if(typeof callback === 'function'){
          callback();
        }
      })
    })
    .catch((error) => {
      console.log("Error getting favorites: ", error);
    });
  };

  handleRemove = (item, favoriteKey, favorite) => {
    this.props.handleRemove(item, favoriteKey, favorite);
  };

  handleEdit = (key) => {
    this.props.handleEdit(key, this.state.newName, () => this.toggleEdit(key, false));
  };

  toggleEdit = (key, bool) => {
    this.setState({ toggleEdit: {[key]: bool} })
  };

  handleNameChange = (event) => {
    this.setState({newName: event.target.value})
  };

  render() {
    const {favorites, user, db} = this.props;
    const {toggleEdit} = this.state;

    const renderRestaurant = (item, index, favoriteKey, favorite) => {
      return <div key={index} style={{display: 'flex', flexDirection: 'row'}}>
        <label>{item.name}</label>
        <button onClick={() => this.handleRemove(item, favoriteKey, favorite)}>Remove</button>
      </div>
    }

    const renderFavorite = (favorite, key) => {
      return (
        <div key={key} style={{display: 'flex', justifyContent: 'flex-start', border: '1px solid black', flexDirection: 'column'}}>
          <div>
          { 
            toggleEdit[key] ? 
            <React.Fragment>
              <input type="text" onChange={this.handleNameChange} />
              <button onClick={() => this.handleEdit(key)}>Ok</button>
              <button onClick={() => this.toggleEdit(key, false)}>Cancel</button>
            </React.Fragment>: 
            <React.Fragment>
              <label>{favorite.name}</label>
              <button onClick={() => this.toggleEdit(key, true)}>Edit</button>
            </React.Fragment>
          }
          <Collaborator favorite={favorite} user={user} db={db} />
          </div>
          {
            favorite.restaurants.map((item, index) => {
              return renderRestaurant(item, index, key, favorite)
            })
          }
        </div>
      )
    }

    return (
      <div style={{border: '2px solid black'}}>
        {
          favorites.length > 0 ?
          favorites.map((item, index) => renderFavorite(item, item.id)) :
          <label>No favorite</label>
        }
      </div>
    );
  }
}

export default FavoriteList;
