import React from 'react'

const FavoriteList = ({data, db}) => {
  const renderRestaurant = (data, key) => {
    return <div key={key}>
      <label>{data.name}</label>
    </div>
  }

  const renderRestaurantList = (ids) => {
    let restaurantRef = db.collection('restaurants');
    let restaurantList = ids.map(item => {
      restaurantRef.doc(item).get()
      .then(doc => {
        if (!doc.exists) {
          console.log('Restaurant not found');
          return null;
        } else {
          return doc.data();
        }
      })
      .catch(err => {
        console.log('Error getting restaurants', err);
        return null;
      });

      return null;
    })

    return (
      <div>
      {
        restaurantList.map((item, key) => {
          return renderRestaurant(item, key)
        })
      }
      </div>
    );
  }

  const renderFavorite = (item, key) => {
    return (
      <div key={key} style={{display: 'flex', justifyContent: 'flex-start', border: '1px solid black'}}>
        <div style={{flex: 1}}>
          <label>{item.name}</label>
          {renderRestaurantList(item.restaurants)}
        </div>
      </div>
    )
  }

  return (
    <div style={{border: '2px solid black'}}>
      {
        data.length > 0 ?
        data.map((item, key) => renderFavorite(item, key)) :
        <label>No favorite</label>
      }
    </div>
  );
}

export default FavoriteList;
