import React, { Component } from 'react';
import "react-datepicker/dist/react-datepicker.css";

class AddFavorite extends Component {
  constructor(props) {
    super(props);
  
    this.state = {
    };
  }

  handleChange = (event) => {
    this.setState({favoriteAs: event.target.value})
  };

  addToFavorite = () => {
    const {db, restaurant, user} = this.props;

    let restaurantRef = db.collection('favorites');
    restaurantRef.where('name', '==', this.state.favoriteAs).get()
    .then(snapshot => {
      if (snapshot.empty) {
        let newItem = {
          name: this.state.favoriteAs,
          owner: user,
          collaborator: [],
          restaurants: [restaurant.id]
        };
        restaurantRef.add(newItem).then(ref => {
          restaurantRef.doc(ref.id).update({id: ref.id});
        })
        return;
      }  

      snapshot.forEach(doc => {
        console.log(doc.id, '=>', doc.data());
        // let isDataOwner = doc.data().owner === user;
        // let isCollaborator = doc.data().collaborator.includes(user);
        if(!doc.data().restaurants.includes(restaurant.id)){
          let updatedData = {
            restaurants: [...doc.data().restaurants, restaurant.id]
          }
          restaurantRef.doc(doc.id).update(updatedData);
          }
      });
    })
    .catch(err => {
      console.log('Error getting documents', err);
    });
  };
 
  render() {
    return (
      <div style={{display: 'flex', flex: 1, flexDirection: 'row'}}>
        <div style={{flex: 1}} onClick={this.addToFavorite}>
          <label>add to favorite</label>
        </div>
        <div style={{flex: 1}}>
          <input type="text" onChange={this.handleChange}></input>
        </div>
      </div>
    );
  }
}

export default AddFavorite;
