import React, { Component } from 'react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const firebase = require("firebase");
// Required for side-effects
require("firebase/firestore");

firebase.initializeApp({
  apiKey: "AIzaSyB9G_v0FY2aFN3VzG9J10TI_wtSc-hEFBg",
  authDomain: "interview-test-ec9c0.firebaseapp.com",
  databaseURL: "https://interview-test-ec9c0.firebaseio.com",
  projectId: "interview-test-ec9c0",
  storageBucket: "interview-test-ec9c0.appspot.com",
  messagingSenderId: "493975126354"
});

// Initialize Cloud Firestore through Firebase
var db = firebase.firestore();

// Disable deprecated features
db.settings({
  timestampsInSnapshots: true
});

class RestaurantList extends Component {
  constructor(props) {
    super(props);
  
    this.state = {
      startDate: new Date()
    };
    this.handleChange = this.handleChange.bind(this);
  }

  getData = () => {
    // restaurantRef.where("capital", "==", true)
    var restaurantRef = db.collection('restaurants');
    restaurantRef.get().then(function(querySnapshot) {
        querySnapshot.forEach(function(doc) {
            // doc.data() is never undefined for query doc snapshots
            console.log(doc.id, " => ", doc.data());
        });
    })
    .catch(function(error) {
        console.log("Error getting documents: ", error);
    });
  }

  handleChange(date) {
    this.setState({
      startDate: date
    }, () => {
      alert('asshole')
      console.log(date)
      this.getData();
    });
  }
 
  render() {
    return (
      <DatePicker
        selected={this.state.startDate}
        onChange={this.handleChange}
      />
    );
  }
}

export default RestaurantList;
