import React, { Component } from 'react';
import "react-datepicker/dist/react-datepicker.css";

class AddFavorite extends Component {
  constructor(props) {
    super(props);
  
    this.state = {
      toggleButton: false
    };
  }

  handleChange = (event) => {
    this.setState({favoriteAs: event.target.value})
  };

  updateRealtime = () => {
    const {realtimeDb} = this.props;
    let currTime = new Date();
    console.log(currTime.valueOf())
    realtimeDb.ref("detectedProcess").set({asd: currTime.valueOf()}).then(resp => console.log(resp))
  };

  addToFavorite = () => {
    const {db, restaurant, user} = this.props;
    const {favoriteAs} = this.state;
    const that = this;

    let restaurantRef = db.collection('favorites');
    restaurantRef.where('name', '==', favoriteAs).get()
    .then(snapshot => {
      if (snapshot.empty) {
        let newItem = {
          name: favoriteAs,
          owner: user,
          collaborator: [],
          restaurants: [restaurant]
        };
        restaurantRef.add(newItem).then(ref => {
          restaurantRef.doc(ref.id).update({id: ref.id}).then(resp => {
            alert('Added to ' + favoriteAs)
            that.updateRealtime();
          });
        })
        return;
      }  

      snapshot.forEach(doc => {
        console.log(doc.id, '=>', doc.data());
        if(!doc.data().restaurants.includes(restaurant.id)){
          let updatedData = {
            restaurants: [...doc.data().restaurants, restaurant]
          }
          restaurantRef.doc(doc.id).update(updatedData).then(resp => {
            alert('Added to ' + favoriteAs)
            that.updateRealtime();
          });
        }
      });
    })
    .catch(err => {
      console.log('Error getting documents', err);
    });
  };

  toggleButton = (bool) => {
    this.setState({toggleButton: bool})
  };
 
  render() {
    const {toggleButton} = this.state;

    return (
      <React.Fragment>
        {
          toggleButton ?
          <div style={{flex: 1}}>
            <input type="text" onChange={this.handleChange}></input>
            <button onClick={this.addToFavorite}>Ok</button>
            <button onClick={() => this.toggleButton(false)}>Cancel</button>
          </div> :
          <div style={{flex: 1}}>
            <button onClick={() => this.toggleButton(true)}>Add To Collection</button>
          </div>
        }
      </React.Fragment>
    );
  }
}

export default AddFavorite;
