import React, { Component } from 'react';
// import Icon, { FontAwesome, Feather } from 'react-web-vector-icons';

class CollaboratorList extends Component {
  constructor(props) {
    super(props);
  
    this.state = {
      toggleEdit: []
    };
  }

  removeItem = () => {

  };

  render() {
    const {favorite, user} = this.props;

    const renderCollaborator = () => {
      return favorite.collaborator.map((item, key) => 
        <div style={{display: 'flex', flexDirection: 'row', border: '1px solid black', borderRadius: 10}}>
          <div>
            <label>{item}</label>
          </div>
          <div onClick={this.removeItem}>
            <label>X</label>
          </div>
        </div>
      )
    };

    return (
      <div>
        {renderCollaborator()}
      </div>
    );
  }
}

export default CollaboratorList;
